package control;import java.io.File;public class LibraryCrawler {		private String dir = "";	private Database db;		private String directory;		public String getDirectory() {		return directory;	}	public void setDirectory(String path) {		dir = path;	}	public LibraryCrawler(Database d) {		db = d;	}		/**	 * Each directory within root is an artist's folder	 * Each subdirectory is an album by this artist	 */		public void crawl() {		File root = new File(dir);		File[] artistList = root.listFiles();		for (File artist : artistList) {			Discography tempDisc = new Discography(artist.getName());						File artistDir = artist.getAbsoluteFile();						File[] albumList = artistDir.listFiles();			for(File album : albumList) {				tempDisc.addAlbum(album.getName());			}			db.push(tempDisc);		}			}}